import React from 'react'
import Card from './Card'

class MyPlans extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            border: "",
            plans: []
        }
    }

    componentDidMount() {
        fetch("http://localhost:5000/users/5e69b32167dbfb1157c1becf/myplans")
            .then(res => res.json())
            .then(plans => {
                this.setState({
                    plans: plans
                })
            })
            .catch(err => console.log(err))
    }

    toggleModify() {
        this.state.border == "border border-danger" ? this.state.border = "border border-success" : this.state.border = "border border-danger"
    }

    genPlanCards() {
        let cards = []
        for (let i = 0; i < this.state.plans.length; i++) {
            cards.push(
                <div className={this.props.border}>
                    <Card plan={this.state.plans[i]} />
                </div>
            )
        }
        return cards
    }

    render() {
        return (
            <div>
                <div className="container mt-3">
                    <h3 className="text-left">Here are your active plans:</h3>
                    <div className="row">
                        {this.genPlanCards()}
                    </div>
                    <div class="text-left mt-3">
                        <button class="btn btn-info" onClick={this.toggleModify()}>Modify Plans</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default MyPlans
