import React from 'react'
import Card from './Card'

class MyPlans extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            plans: [],
            box: {}
        }
    }

    componentDidMount() {
        fetch("http://localhost:5000/users/5e69b32167dbfb1157c1becf/myplans")
            .then(res => res.json())
            .then(plans => {
                this.setState({
                    plans: plans
                })
            })
            .catch(err => console.log(err))
    }

    genPlanCards() {
        let cards = []
        for (let i = 0; i < this.state.plans.length; i++) {
            cards.push(
                <div className={this.props.border}>
                    <Card plan={this.state.plans[i]} select={this.select} />
                    {this.props.toggle ? this.props.button : ""}
                </div>
            )
        }
        return cards
    }

    select = (name, plan) => {
        console.log(this.state)
        // name in this.state.box ? delete this.state.box[name] : this.state.box[name] = plan
        if (name in this.state.box) {
            delete this.state.box[name]
        }else {
             this.state.box[name] = plan
        }
    }

    render() {
        return (
            <div>
                <div className="container mt-3">
                    <h3 className="text-left">Here are your active plans:</h3>
                    <div className="row">
                        {this.genPlanCards()}
                    </div>
                    <div class="text-left mt-3">
                        <button class="btn btn-info" onClick={e => this.toggleModify()}>Modify Plans</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default MyPlans
